page_settings.custom_css:
:root {
  --primary-color: #E3B967;
  --secondary-color: #231F20;
  --third-color: #c49b56;
  --fourth-color: #F1F1F1;
  --primary-color-light: #f8edd7;
  --primary-color-dark: #c99225;
  --secondary-color-light: #3e3739;
  --secondary-color-dark: black;
  --contrast-color: #AC2716;
  --text-color: #231F20;
  --light-color: #F1F1F1;
}

* {
  transition: 0.2s;
}

html {
  font-size: 18px;
  scroll-padding-top: 72px;
}

body {
  font-family: "Inter", sans-serif;
  color: var(--text-color);
}

h1, h2, h3, h4, h5, h6 {
  font-family: "Inter", sans-serif;
}

img {
  max-width: -moz-fit-content;
  max-width: fit-content;
  height: auto;
}

figure {
  margin-bottom: 0;
}

.container {
  max-width: 1200px;
}

.fw-bold {
  font-weight: 800 !important;
}

@media (max-width: 499px) {
  .navbar-brand {
    max-width: 160px;
  }
}

.caret li {
  position: relative;
  padding-left: 6px;
  list-style: none;
}
.caret li::before {
  content: "\f231";
  font-family: "bootstrap-icons";
  position: absolute;
  top: -0.6em;
  left: -1em;
  color: var(--primary-color);
  font-size: 1.6em;
  transform: translateY(10px) !important;
}

.item {
  transition: 0.2s;
}
.item:hover {
  scale: 1.025;
}

.button-bg {
  background-image: linear-gradient(to top, #b4e5ba 50%, #c7e7bc 51%);
  color: var(--text-color) !important;
  border: 2px solid #8abfa5 !important;
}

.button-bg:hover {
  background-image: linear-gradient(to bottom, #b4e5ba 50%, #c7e7bc 51%);
}
.button-bg.promo {
  background-image: linear-gradient(to top, #fccd15 50%, #ffd814 51%);
  color: var(--text-color) !important;
  border: 2px solid #a78a19 !important;
}
.button-bg.promo:hover {
  background-image: linear-gradient(to bottom, #fccd15 50%, #ffd814 51%);
}

@media (max-width: 475px) {
  .accordion-button {
    font-size: 1rem !important;
  }
}
.accordion-button:not(.collapsed) {
  background-color: white;
  color: var(--text-color);
}
.accordion-button:focus {
  box-shadow: 0 0 0 0.25rem transparent;
}

#faq .accordion-item {
  border-radius: 0;
}
#faq .accordion-button {
  font-weight: bold;
  border-radius: 0;
  border-bottom: 2px solid var(--primary-color);
}

#ingredients img {
  max-width: 100%;
}

#references * {
  font-weight: 500;
  word-break: break-all;
}
#references img {
  width: auto;
  height: 50px;
}
@media (max-width: 599px) {
  #references img {
    height: 30px;
  }
}
#references span {
  color: #8f8f8f;
}

.fw-bolder {
  font-weight: 900;
}

.triangle {
  position: relative;
  width: 0;
  height: 0;
  border-left: 40px solid transparent;
  border-right: 40px solid transparent;
  border-top: 40px solid var(--primary-color);
  margin: -1rem auto 1rem;
}

#carouselReviews i.bi.bi-quote {
  font-size: 5em;
  color: var(--primary-color);
}
@media (max-width: 575px) {
  #carouselReviews i.bi.bi-quote {
    font-size: 2.5em;
  }
}
#carouselReviews i.bi.bi-quote.top {
  top: -0.5em;
  left: -0.5em;
}
#carouselReviews i.bi.bi-quote.bottom {
  bottom: -0.5em;
  right: -0.5em;
}
#carouselReviews .carousel-control-next, #carouselReviews .carousel-control-prev {
  filter: brightness(0.5) sepia(1) hue-rotate(125deg);
}
#carouselReviews .carousel-control-next-icon, #carouselReviews .carousel-control-prev-icon {
  height: 50px;
  width: 50px;
}
#carouselReviews .carousel-indicators [data-bs-target] {
  width: 14px;
  height: 14px;
  border-radius: 50%;
  background-color: var(--primary-color);
}
